mapper.select:
select OrderID,HotelName,UserID,SerialNumber,BookDate,OrderStatus,CreateDate from zl_cleanorder where 1 = 1 and IsDelete = '0'
and UserID = #{userId}
and OrderStatus = #{orderstatus}
order by CreateDate DESC

select InvoiceID, UserID, InvoiceType, MainBodyType, Title, Identifier, Tel, Remark, InvoiceStatus, IsDelete, CreateDate, UpdateDate from zl_invoice where 1 = 1 and IsDelete = '0'
and UserID = #{userId}
and InvoiceStatus = #{invoicestatus}

select OrderID, UserID, SerialNumber, HotelID, HotelName, RoomID, RoomNumber, ImgUrls, Remark, ComeFormID, IsDelete, OrderStatus, CreateDate, UpdateDate from zl_repairorder where 1 = 1 and IsDelete = '0'
and UserID = #{userId}
and OrderStatus = #{orderstatus}
order by CreateDate DESC

select s.OrderID, s.SerialNumber, s.HotelID, s.HotelName, s.RoomID, s.RoomNumber, s.Remark, s.OrderStatus, s.CreateDate, s.UpdateDate, sd.OrderDetailID, sd.OrderID, sd.GoodsID, sd.GoodsName, sd.GoodsCoverUrl, sd.Price, sd.GoodsCount from zl_serviceorder s left join zl_serviceorderdetail sd on s.OrderID = sd.OrderID where 1 = 1 and s.IsDelete = 0 and s.IsUserDelete = 0
and s.UserID = #{userId}
and s.OrderStatus = #{orderstatus}
order by CreateDate DESC
