mapper.select:
select zg.GoodsID, zg.GoodsName, zg.CoverImgUrl, zg.OriginalPrice, zg.CurrentPrice, zg.TotalStockCount, zg.TotalSoldCount, zg.TotalVirtualSoldCount from zl_goods zg left join zl_goodscategory zgc on zg.GoodsCategoryID=zgc.GoodsCategoryID where zg.GoodsStatus=1 and zg.IsDelete=0 and zgc.IsDelete=0 and zg.HotelID=#{hotelId} and zgc.BelongModule=#{belongModule}
and zg.IsRecommand=1
and zgc.CategoryName=#{categoryName}
order by zg.Sort asc
