mapper.select:
select zhgs.HotelGoodsSkuID, zg.GoodsName, zg.CoverImgUrl, zg.OriginalPrice, zg.CurrentPrice, zhgs.SoldCount, zhgs.VirtualSoldCount from zl_hotelgoodssku zhgs left join zl_goods zg on zhgs.GoodsID=zg.GoodsID left join zl_goodscategory zgc on zg.GoodsCategoryID=zgc.GoodsCategoryID where zhgs.IsDelete=0 and zhgs.IsDefault=1 and zg.GoodsStatus!='-1' and zgc.IsDelete=0 and zgc.CategoryStatus=1 and zhgs.HotelID=#{hotelId} and zgc.BelongModule=#{belongModule}
and zg.IsRecommand=1
and zgc.CategoryName=#{categoryName}
order by zg.Sort asc

mapper.update:
UPDATE
  zl_goodssku
SET
  StockCount = StockCount - #{goodsCount},
  SoldCount = SoldCount + #{goodsCount}
WHERE SkuID = #{skuID}

UPDATE
  zl_hotelgoodssku
SET
  StockCount = StockCount - #{goodsCount},
  SoldCount = SoldCount + #{goodsCount}
WHERE SkuID = #{skuID}
AND HotelID = #{hotelID}
