mapper.select:
SELECT
  SUM(StockCount)
FROM
  zl_hotelgoodssku
WHERE GoodsID = #{goodsid}
  AND HotelID = #{hotelid}

SELECT zg.`GoodsID`, zg.`GoodsName`, zg.`CoverImgUrl`, MIN(zgs.`OriginalPrice`) OriginalPrice, MIN(zgs.`CurrentPrice`) CurrentPrice, zg.`GoodsCategoryID`, zg.`BelongModule`, zg.GoodsStatus, zhgs.`HotelID`, SUM(zhgs.`StockCount`) StockCount FROM zl_hotelgoodssku zhgs LEFT JOIN zl_goodssku zgs ON zhgs.GoodsID = zgs.GoodsID LEFT JOIN zl_goods zg ON zgs.GoodsID = zg.GoodsID WHERE zhgs.IsDelete = 0 AND zgs.IsDelete = 0 AND zg.CheckStatus = 2 AND zg.IsDelete = 0 AND ( zg.GoodsStatus = 0 OR zg.GoodsStatus = 1 ) AND zg.GoodsName LIKE concat('%',#{selectParam},'%')
AND zg.`BelongModule` = #{belongModule}
GROUP BY zg.GoodsName
