mapper.select:
select zsg.GoodsID, zsg.GoodsName, zsg.Tags, zsg.CoverImgUrl, zsg.ApplyMaxCount, zsg.ApplyLimitCount, zsg.SalePrice, zsg.OriginalPrice, zsg.MarketPrice from zl_servicegoods zsg left join zl_servicegoodscategory zsgc on zsg.GoodsCategoryID=zsgc.GoodsCategoryID where zsg.CheckStatus=2 and zsg.GoodsStatus=1 and zsg.IsDelete=0 and zsgc.CategoryStatus=1 and zsgc.IsDelete=0 and zsg.HotelID=#{hotelId} and zsgc.BelongModule=#{belongModule}
and zsgc.CategoryName=#{categoryName}
order by zsg.Sort asc,zsg.UpdateDate desc
